static uint16_t unbiased_rand(uint16_t max) {
    uint16_t remainder = RAND_MAX % max;
    uint16_t x;
    do {
        x = rand();
    } while(x >= RAND_MAX - remainder);
    return x % max;
}

struct Spread {
    int card[3];
    bool selected[3];
};

struct Spread spread;

void draw_tarot(void* context) {
    App* app = context;
    widget_reset(app->widget);

    // Set the cursor to the selected card
    spread.selected[0] = 0;
    spread.selected[1] = 0;
    spread.selected[2] = 0;
    spread.selected[card_selected] = 1;

    // Draw cards
    widget_add_icon_element(
        app->widget,
        (128 - card_x) / 2 - 32,
        10 - 2 * spread.selected[0],
        card[spread.card[0]].icon);
    widget_add_icon_element(
        app->widget, (128 - card_x) / 2, 10 - 2 * spread.selected[1], card[spread.card[1]].icon);
    widget_add_icon_element(
        app->widget,
        (128 - card_x) / 2 + 32,
        10 - 2 * spread.selected[2],
        card[spread.card[2]].icon);

    // Draw cursor
    widget_add_icon_element(
        app->widget, (128 - card_x) / 2 - 34 + card_x / 2 + card_selected * 32, 41, &I_cursor);

    // Draw card name
    widget_add_string_element(
        app->widget,
        64,
        60,
        AlignCenter,
        AlignBottom,
        FontPrimary,
        card[spread.card[card_selected]].name);
}

static bool widget_input_callback(InputEvent* input_event, void* context) {
    App* app = context;
    bool consumed = false;
    if(input_event->type == InputTypeShort) {
        switch(input_event->key) {
        case InputKeyRight:
            card_selected++;
            if(card_selected > 2) {
                card_selected = 0;
            }
            consumed = true;
            break;
        case InputKeyLeft:
            card_selected--;
            if(card_selected < 0) {
                card_selected = 2;
            }
            consumed = true;
            break;
        case InputKeyUp:
            // UP
            //consumed = true;
            break;
        case InputKeyDown:
            // DOWN
            //consumed = true;
            break;
        default:
            consumed = false;
            break;
        }
    }
    if(consumed) draw_tarot(app);
    return consumed;
}

void tarot_app_scene_on_enter_game(void* context) {
    FURI_LOG_T(TAG, "tarot_app_scene_on_enter_game");
    App* app = context;

    // Set random spread
    spread.card[0] = unbiased_rand(card_number);
    spread.card[1] = unbiased_rand(card_number);
    while(spread.card[1] == spread.card[0]) {
        spread.card[1] = unbiased_rand(card_number);
    };
    spread.card[2] = unbiased_rand(card_number);
    while(spread.card[2] == spread.card[0] || spread.card[2] == spread.card[1]) {
        spread.card[2] = unbiased_rand(card_number);
    }

    // Upside down card option
    if(0 /*settings.upside_down*/) {
        spread.card[0] += card_number * unbiased_rand(2);
        spread.card[1] += card_number * unbiased_rand(2);
        spread.card[2] += card_number * unbiased_rand(2);
    }

    draw_tarot(app);

    view_set_context(widget_get_view(app->widget), app);

    view_set_input_callback(widget_get_view(app->widget), widget_input_callback);

    view_dispatcher_switch_to_view(app->view_dispatcher, AppView_Widget);
}

